/* promise的简单实现 */
class myPromise{
    constructor (process) {
        this.status = 'pending';
        this.msg = '';
        process(this.resolve.bind(this), this.reject.bind(this));
        return this;
    }
    resolve (val) {
        this.status = 'resolved';
        this.msg = val;
    }
    reject (err) {
        this.status = 'rejected';
        this.msg = err;
    }
    then (resolve, reject) {
        if(this.status == 'resolved')
            resolve(this.msg);
        if(this.status == 'rejected')
            reject(this.msg);
    }
}

